using System.Collections; using System.Collections.Generic; using UnityEngine;  public class VRLookWalk : MonoBehaviour {  	// Initialisieren der Geschwindigkeit und des Winkels, bei dem die Bewegung gestartet werden soll 	public Transform vrCamera; 	public float toggleAngle = 30.0f; 	public float speed = 3.0f; 	public bool moveForward;  	private CharacterController cc;  	// Bei der Bewegung wird auf den Charactercontroller zurükgegriffen, weil dort schon einige Voreinstellungen hinterlegt sind. 	void Start() 	{ 		cc = GetComponent<CharacterController>(); 	}  	// Update is called once per frame / Es wird größenteils auf bereits bestehende Funktionen in Unity zurük gegriffen. Mit if/else wird anhand des Kamerawinkels 	// geprüt, ob die SimpleMove-Funktion ausgelöt werden soll. 	void Update() 	{ 		if (vrCamera.eulerAngles.x >= toggleAngle && vrCamera.eulerAngles.x < 90.0f) 		{ 			moveForward = true; 		} 		else 		{ 			moveForward = false; 		}  		if (moveForward ) 		{ 			Vector3 forward = vrCamera.TransformDirection(Vector3.forward);  			cc.SimpleMove(forward * speed); 		} 	} } 